import React, {useState, useEffect} from "react";
import "./App.css";

//importing components
import Form from "./components/Form.js"
import TodoList from "./components/TodoList.js"

// ma
function App(){
  const [inputText,setInputText] = useState("");
  const [todos, setTodos] = useState([]);

  const [status, setStatus] = useState("all");
  const [filteredTodos, setFilteredTodos] = useState([]);

  const filterHandler = () => {
    switch(status){
      case "completed":
        setFilteredTodos(todos.filter(todo => todo.completed === true));
        break;
      case "uncompleted":
        setFilteredTodos(todos.filter(todo => todo.completed === false));
        break;
      case "all":
        setFilteredTodos(todos);
        break;
    }
    console.log("status is: " + status);
    console.log(filteredTodos);
    console.log("done status: " + status);
  }
  useEffect(()=>{
    filterHandler();
  }, [todos, status]);

  return(
    <div className="App">

      {/* <iframe src="https://docs.google.com/spreadsheets/d/e/2PACX-1vRZgM3BlebedB4DLVBP3jAk88BbdJs4YWiBDREDbY7ZTZHkpqHD14TOWPjDzaHh6ZEiYsAUe8Z6fFBV/pubhtml?widget=true&amp;headers=false" width="1000" height="600"></iframe> */}

      <header> 
        <h1>Todo List.</h1>
      </header>

      <Form 
        todos={todos} 
        setTodos={setTodos} 
        setInputText={setInputText} 
        inputText={inputText}
        status={status}
        setStatus={setStatus}
        />
      <TodoList 
        filteredTodos={filteredTodos}
        setTodos={setTodos} 
        todos={todos}/>
    </div>

  )
}
export default App;
