    }

  }

  return(           // main
    <div className="App">

      <header> 
        <h1>Todo List.</h1>
      </header>

      <Form 
        todos={todos} 
        setTodos={setTodos} 
        setInputText={setInputText} 
        inputText={inputText}
        status={status}
        setStatus={setStatus}
      />
      <TodoList 
        filteredTodos={filteredTodos}
        setTodos={setTodos} 
        todos={todos}
      />

    </div>
  )
}
export default App;
